def add_genre(id, name):
    g = Genre.objects.get_or_create(genre_id=id, name=name)[0]
    g.save()
    print(g.genre_id);
    print(g.name);
    return g

def add_videogame(name, id, genre, description, rating, release, developer, publisher, image):
    print(name);
    print(id);
    print(genre);
    print(description);
    print(rating);
    print(release);
    print(developer);
    print(publisher);
    print(image);
    v = VideoGame.objects.get_or_create(name=name, slug=slugify(name), id=id, genre=genre, description=description, rating=rating, release=release, developer=developer, publisher=publisher, image=image)[0]
    v.save()
    return v


